      CALL cmd_unit_set_health(g_id,p_num,board_unit_id);
    END IF;


    SET cmd_log=REPLACE(cmd_log,'$log_unit',log_unit(board_unit_id));
    SET cmd_log=REPLACE(cmd_log,'$log_health',log_health(drink_health_amt));
